import React, { useState } from "react";
import { evaluate } from "mathjs";
import { FaPlusCircle, FaMinusCircle, FaSortAmountDown, FaSortAmountUp, FaHashtag as FaCount, FaSave, FaFolderOpen, FaEdit, FaRegCheckSquare, FaTimesCircle, FaTextHeight, FaSortAlphaDown, FaCut, FaEraser, FaSearch } from "react-icons/fa";
import "./spreadSheet.css"

const Spreadsheet = () => {
    const [cells, setCells] = useState(Array(10).fill(null).map(() => Array(10).fill("")));
    const [selectedCell, setSelectedCell] = useState(null);
    const [formula, setFormula] = useState("");
    const [isFormulaMode, setIsFormulaMode] = useState(false);

    const handleCellChange = (row, col, value) => {
        const newCells = cells.map((r, i) => r.map((c, j) => (i === row && j === col ? value : c)));
        setCells(newCells);
    };

    const validateCell = (value) => {
        if (!isNaN(value)) return null;
        return <FaTimesCircle color="red" title="Invalid Value" />;
    };

    const applyMathFunction = (type) => {
        if (!selectedCell) return;

        let values = cells.flat().map(value => parseFloat(value)).filter(value => !isNaN(value));
        if (values.length === 0) return;

        let result = 0;
        switch (type) {
            case "SUM":
                result = values.reduce((acc, val) => acc + val, 0);
                break;
            case "AVG":
                result = values.length ? (values.reduce((acc, val) => acc + val, 0) / values.length) : 0;
                break;
            case "MIN":
                result = Math.min(...values);
                break;
            case "MAX":
                result = Math.max(...values);
                break;
            case "COUNT":
                result = values.length;
                break;
            default:
                return;
        }

        const [targetRow, targetCol] = selectedCell;
        const newCells = [...cells];
        newCells[targetRow][targetCol] = result.toString();
        setCells(newCells);
    };

    return (
        <div className="spreadsheet">
            <div className="toolbar">
                <button onClick={() => applyMathFunction("SUM")}><FaPlusCircle /> Sum</button>
                <button onClick={() => applyMathFunction("AVG")}><FaMinusCircle /> Average</button>
                <button onClick={() => applyMathFunction("MIN")}><FaSortAmountDown /> Min</button>
                <button onClick={() => applyMathFunction("MAX")}><FaSortAmountUp /> Max</button>
                <button onClick={() => applyMathFunction("COUNT")}><FaCount /> Count</button>
                <button><FaSave /> Save</button>
                <button><FaFolderOpen /> Load</button>
                <button onClick={() => setIsFormulaMode(true)}><FaEdit /> Enter Formula</button>
                <button><FaTextHeight /> Upper</button>
                <button><FaSortAlphaDown /> Lower</button>
                <button><FaCut /> Trim</button>
                <button><FaEraser /> Remove Duplicates</button>
                <button><FaSearch /> Find & Replace</button>
                {isFormulaMode && (
                    <>
                        <input type="text" value={formula} placeholder="Enter formula (e.g. A1+B1)" />
                        <button><FaRegCheckSquare /> Apply Formula</button>
                    </>
                )}
            </div>
            <table>
                <thead>
                    <tr>
                        <th></th>
                        {cells[0].map((_, colIndex) => (
                            <th key={colIndex}>{String.fromCharCode(65 + colIndex)}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {cells.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                            <th>{rowIndex + 1}</th>
                            {row.map((cell, colIndex) => (
                                <td key={colIndex} className="cell-container">
                                    <input
                                        value={cell}
                                        onChange={(e) => handleCellChange(rowIndex, colIndex, e.target.value)}
                                        onClick={() => setSelectedCell([rowIndex, colIndex])}
                                    />
                                    <span className="validation-icon">{validateCell(cell)}</span>
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Spreadsheet;
