import React, { useState } from "react";
import { evaluate } from "mathjs";
import { FaBold, FaItalic, FaFont, FaTrash, FaPlus, FaMinus, FaTable, FaSortNumericDown, FaArrowUp, FaArrowDown, FaCut, FaSearch, FaEllipsisH, FaCalculator, FaPlusCircle, FaMinusCircle, FaChartBar, FaHashtag, FaSave, FaFolderOpen, FaTextHeight, FaTextWidth, FaSortAmountDown, FaSortAmountUp, FaHashtag as FaCount, FaEdit, FaRegCheckSquare } from "react-icons/fa";
import "./spreadSheet.css"

const Spreadsheet = () => {
    const [cells, setCells] = useState(Array(10).fill(null).map(() => Array(10).fill("")));
    const [selectedCells, setSelectedCells] = useState([]);
    const [formula, setFormula] = useState("");
    const [isFormulaMode, setIsFormulaMode] = useState(false);

    const handleCellChange = (row, col, value) => {
        const newCells = cells.map((r, i) => r.map((c, j) => (i === row && j === col ? value : c)));
        setCells(newCells);
    };

    const handleFormulaInputChange = (e) => {
        setFormula(e.target.value);
    };

    const applyFormula = () => {
        if (selectedCells.length === 0 || formula === "") return;

        try {
            const [row, col] = selectedCells[0];
            const newCells = [...cells];
            const result = evaluate(formula.replace(/[A-J][0-9]+/g, (match) => {
                const colLetter = match[0];
                const rowNumber = match.slice(1);
                const r = parseInt(rowNumber, 10) - 1;
                const c = colLetter.charCodeAt(0) - 65;
                return cells[r]?.[c] || 0;
            }));

            newCells[row][col] = result.toString();
            setCells(newCells);
            setIsFormulaMode(false);
        } catch (error) {
            alert("Error evaluating formula. Please check your syntax.");
        }
    };

    const applyTextTransformation = (type) => {
        const [row, col] = selectedCells[0] || [];
        if (row === undefined || col === undefined) return;

        const value = cells[row][col] || "";
        let transformedValue = value;

        if (type === "UPPER") transformedValue = value.toUpperCase();
        if (type === "LOWER") transformedValue = value.toLowerCase();
        if (type === "TRIM") transformedValue = value.trim();

        const newCells = [...cells];
        newCells[row][col] = transformedValue;
        setCells(newCells);
    };

    const applyMathFunction = (type) => {
    if (selectedCells.length === 0) return;

    // Extract values from selected cells and convert to numbers
    let values = selectedCells.map(([row, col]) => {
        let value = parseFloat(cells[row][col]); // Convert to number
        return isNaN(value) ? 0 : value; // Default to 0 if not a number
    });

    let result = 0;

    switch (type) {
        case "SUM":
            result = values.reduce((acc, val) => acc + val, 0);
            break;
        case "AVG":
            result = values.length ? values.reduce((acc, val) => acc + val, 0) / values.length : 0;
            break;
        case "MIN":
            result = values.length ? Math.min(...values) : 0;
            break;
        case "MAX":
            result = values.length ? Math.max(...values) : 0;
            break;
        case "COUNT":
            result = values.length;
            break;
        default:
            return;
    }

    // Drop result in the last clicked cell
    const [targetRow, targetCol] = selectedCells[selectedCells.length - 1];
    const newCells = [...cells];
    newCells[targetRow][targetCol] = result.toString();
    setCells(newCells);
};



    const saveSpreadsheet = () => {
        localStorage.setItem("spreadsheetData", JSON.stringify(cells));
        alert("Spreadsheet saved successfully!");
    };

    const loadSpreadsheet = () => {
        const data = localStorage.getItem("spreadsheetData");
        if (data) {
            setCells(JSON.parse(data));
            alert("Spreadsheet loaded successfully!");
        } else {
            alert("No saved spreadsheet found!");
        }
    };

    return (
        <div className="spreadsheet">
            <div className="toolbar">
                <button onClick={() => applyTextTransformation("UPPER")}><FaArrowUp /> Uppercase</button>
                <button onClick={() => applyTextTransformation("LOWER")}><FaArrowDown /> Lowercase</button>
                <button onClick={() => applyTextTransformation("TRIM")}><FaCut /> Trim</button>
                <button onClick={() => applyMathFunction("SUM")}><FaPlusCircle /> Sum</button>
                <button onClick={() => applyMathFunction("AVG")}><FaMinusCircle /> Average</button>
                <button onClick={() => applyMathFunction("MIN")}><FaSortAmountDown /> Min</button>
                <button onClick={() => applyMathFunction("MAX")}><FaSortAmountUp /> Max</button>
                <button onClick={() => applyMathFunction("COUNT")}><FaCount /> Count</button>
                <button onClick={saveSpreadsheet}><FaSave /> Save</button>
                <button onClick={loadSpreadsheet}><FaFolderOpen /> Load</button>
                <button onClick={() => setIsFormulaMode(true)}>
                    <FaEdit /> Enter Formula
                </button>
                {isFormulaMode && (
                    <>
                        <input
                            type="text"
                            value={formula}
                            onChange={handleFormulaInputChange}
                            placeholder="Enter formula (e.g. A1+B1)"
                        />
                        <button onClick={applyFormula}>
                            <FaRegCheckSquare /> Apply Formula
                        </button>
                    </>
                )}
            </div>
            <table>
                <thead>
                    <tr>
                        <th></th>
                        {cells[0].map((_, colIndex) => (
                            <th key={colIndex}>{String.fromCharCode(65 + colIndex)}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {cells.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                            <th>{rowIndex + 1}</th>
                            {row.map((cell, colIndex) => (
                                <td key={colIndex}>
                                    <input
                                        value={cell}
                                        onChange={(e) => handleCellChange(rowIndex, colIndex, e.target.value)}
                                        onClick={() => setSelectedCells([[rowIndex, colIndex]])}
                                    />
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Spreadsheet;
