import React, { useState } from "react";
import { evaluate } from "mathjs";
import { FaPlus, FaPlusCircle, FaMinusCircle, FaSortAmountDown, FaSortAmountUp, FaHashtag as FaCount, FaSave, FaFolderOpen, FaEdit, FaRegCheckSquare, FaChartBar } from "react-icons/fa";
import "./spreadSheet.css"

const Spreadsheet = () => {
    const [cells, setCells] = useState(Array(10).fill(null).map(() => Array(10).fill("")));
    const [selectedCell, setSelectedCell] = useState(null);
    const [formula, setFormula] = useState("");
    const [isFormulaMode, setIsFormulaMode] = useState(false);

    const handleCellChange = (row, col, value) => {
        const newCells = cells.map((r, i) => r.map((c, j) => (i === row && j === col ? value : c)));
        setCells(newCells);
    };

    const handleFormulaInputChange = (e) => {
        setFormula(e.target.value);
    };

    const applyFormula = () => {
        if (!selectedCell || formula === "") return;

        try {
            const [row, col] = selectedCell;
            const newCells = [...cells];
            const result = evaluate(formula.replace(/[A-J][1-9]/g, (match) => {
                const r = parseInt(match[1], 10) - 1;
                const c = match.charCodeAt(0) - 65;
                return cells[r][c] || 0;
            }));

            newCells[row][col] = result.toString();
            setCells(newCells);
            setIsFormulaMode(false);
        } catch (error) {
            alert("Error evaluating formula. Please check your formula syntax.");
        }
    };

    const addRow = () => {
        setCells([...cells, Array(cells[0].length).fill("")]);
    };

    const addColumn = () => {
        setCells(cells.map(row => [...row, ""]));
    };

    return (
        <div className="spreadsheet">
            <div className="toolbar">
                <button onClick={addRow}><FaPlus /> Add Row</button>
                <button onClick={addColumn}><FaPlus /> Add Column</button>
                <button onClick={() => setIsFormulaMode(true)}><FaEdit /> Enter Formula</button>
                {isFormulaMode && (
                    <>
                        <input type="text" value={formula} onChange={handleFormulaInputChange} placeholder="Enter formula (e.g. A1+B1)" />
                        <button onClick={applyFormula}><FaRegCheckSquare /> Apply Formula</button>
                    </>
                )}
            </div>
            <table>
                <thead>
                    <tr>
                        <th></th>
                        {cells[0].map((_, colIndex) => (
                            <th key={colIndex}>{String.fromCharCode(65 + colIndex)}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {cells.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                            <th>{rowIndex + 1}</th>
                            {row.map((cell, colIndex) => (
                                <td key={colIndex}>
                                    <input
                                        value={cell}
                                        onChange={(e) => handleCellChange(rowIndex, colIndex, e.target.value)}
                                        onClick={() => setSelectedCell([rowIndex, colIndex])}
                                    />
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Spreadsheet;
