import React, { useState } from "react";
import { evaluate } from "mathjs";
import { FaPlusCircle, FaMinusCircle, FaSortAmountDown, FaSortAmountUp, FaHashtag as FaCount, FaSave, FaFolderOpen, FaEdit, FaRegCheckSquare, FaCheckCircle, FaExclamationTriangle, FaTimesCircle, FaTextHeight, FaSortAlphaDown, FaCut, FaEraser, FaSearch } from "react-icons/fa";
import "./spreadSheet.css"

const Spreadsheet = () => {
    const [cells, setCells] = useState(Array(10).fill(null).map(() => Array(10).fill("")));
    const [selectedCell, setSelectedCell] = useState(null);
    const [formula, setFormula] = useState("");
    const [isFormulaMode, setIsFormulaMode] = useState(false);

    const handleCellChange = (row, col, value) => {
        const newCells = cells.map((r, i) => r.map((c, j) => (i === row && j === col ? value : c)));
        setCells(newCells);
    };

    const validateCell = (value) => {
        if (value === "") return <FaExclamationTriangle color="orange" title="Empty Cell" />;
        if (!isNaN(value)) return <FaCheckCircle color="green" title="Valid Number" />;
        return <FaTimesCircle color="red" title="Invalid Value" />;
    };

    const applyTextTransformation = (type) => {
        if (!selectedCell) return;

        const [row, col] = selectedCell;
        let value = cells[row][col];
        if (!value) return;

        switch (type) {
            case "UPPER":
                value = value.toUpperCase();
                break;
            case "LOWER":
                value = value.toLowerCase();
                break;
            case "TRIM":
                value = value.trim();
                break;
            case "REMOVE_DUPLICATES":
                value = [...new Set(value.split(" "))].join(" ");
                break;
            case "FIND_REPLACE":
                const find = prompt("Enter text to find:");
                const replace = prompt("Enter replacement text:");
                value = value.replace(new RegExp(find, "g"), replace);
                break;
            default:
                return;
        }

        const newCells = [...cells];
        newCells[row][col] = value;
        setCells(newCells);
    };

    return (
        <div className="spreadsheet">
            <div className="toolbar">
                <button><FaPlusCircle /> Sum</button>
                <button><FaMinusCircle /> Average</button>
                <button><FaSortAmountDown /> Min</button>
                <button><FaSortAmountUp /> Max</button>
                <button><FaCount /> Count</button>
                <button><FaSave /> Save</button>
                <button><FaFolderOpen /> Load</button>
                <button onClick={() => setIsFormulaMode(true)}><FaEdit /> Enter Formula</button>
                <button onClick={() => applyTextTransformation("UPPER")}><FaTextHeight /> Upper</button>
                <button onClick={() => applyTextTransformation("LOWER")}><FaSortAlphaDown /> Lower</button>
                <button onClick={() => applyTextTransformation("TRIM")}><FaCut /> Trim</button>
                <button onClick={() => applyTextTransformation("REMOVE_DUPLICATES")}><FaEraser /> Remove Duplicates</button>
                <button onClick={() => applyTextTransformation("FIND_REPLACE")}><FaSearch /> Find & Replace</button>
                {isFormulaMode && (
                    <>
                        <input type="text" value={formula} placeholder="Enter formula (e.g. A1+B1)" />
                        <button><FaRegCheckSquare /> Apply Formula</button>
                    </>
                )}
            </div>
            <table>
                <thead>
                    <tr>
                        <th></th>
                        {cells[0].map((_, colIndex) => (
                            <th key={colIndex}>{String.fromCharCode(65 + colIndex)}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {cells.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                            <th>{rowIndex + 1}</th>
                            {row.map((cell, colIndex) => (
                                <td key={colIndex} className="cell-container">
                                    <input
                                        value={cell}
                                        onChange={(e) => handleCellChange(rowIndex, colIndex, e.target.value)}
                                        onClick={() => setSelectedCell([rowIndex, colIndex])}
                                    />
                                    <span className="validation-icon">{validateCell(cell)}</span>
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Spreadsheet;
